import React, { Component } from 'react';
import '../../Containers/DashBoard/DashStyle.css'; // Import your CSS file if needed
import TogSide from '../Animate/toggleSide/TogSide';
import PlusAnimate from '../Animate/plus/PlusAnimate';
import Arrow from '../arrow/Arrow';


class SidebarComponent extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isSidebarHidden: false
    };
  }

  componentDidMount() {
    const sidebar = document.getElementById('sidebar');
    sidebar.addEventListener('mouseleave', this.handleMouseLeave);
    sidebar.addEventListener('mouseenter', this.handleMouseEnter);
  }

  componentWillUnmount() {
    const sidebar = document.getElementById('sidebar');
    sidebar.removeEventListener('mouseleave', this.handleMouseLeave);
    sidebar.removeEventListener('mouseenter', this.handleMouseEnter);
  }

  handleDropdownClick = (e) => {
    e.preventDefault();
    const allDropdowns = document.querySelectorAll('#sidebar .side-dropdown');

    if (!e.target.classList.contains('active')) {
      allDropdowns.forEach(i => {
        const aLink = i.parentElement.querySelector('a:first-child');

        aLink.classList.remove('active');
        i.classList.remove('show');
      });
    }

    e.target.classList.toggle('active');
    e.target.nextElementSibling.classList.toggle('show');
  };

  toggleSidebar = (e) => {
    const allSideDivider = document.querySelectorAll('#sidebar .divider');
    const sidebar = document.getElementById('sidebar');

    if (sidebar.classList.contains('hide')) {
      allSideDivider.forEach(item => {
        item.textContent = '-';
      });
      const allDropdowns = document.querySelectorAll('#sidebar .side-dropdown');
      allDropdowns.forEach(item => {
        const a = item.parentElement.querySelector('a:first-child');
        a.classList.remove('active');
        item.classList.remove('show');
      });
    } else {
      allSideDivider.forEach(item => {
        item.textContent = item.dataset.text;
      });
    }

    sidebar.classList.toggle('hide');
  };

  handleMouseLeave = () => {
    if (this.state.isSidebarHidden) {
      const allDropdowns = document.querySelectorAll('#sidebar .side-dropdown');
      const allSideDivider = document.querySelectorAll('#sidebar .divider');
      allDropdowns.forEach(item => {
        const a = item.parentElement.querySelector('a:first-child');
        a.classList.remove('active');
        item.classList.remove('show');
      });
      allSideDivider.forEach(item => {
        item.textContent = '-';
      });
    }
  };

  handleMouseEnter = () => {
    if (this.state.isSidebarHidden) {
      const allDropdowns = document.querySelectorAll('#sidebar .side-dropdown');
      const allSideDivider = document.querySelectorAll('#sidebar .divider');
      allDropdowns.forEach(item => {
        const a = item.parentElement.querySelector('a:first-child');
        a.classList.remove('active');
        item.classList.remove('show');
      });
      allSideDivider.forEach(item => {
        item.textContent = item.dataset.text;
      });
    }
  };

  handleSidebarToggle = () => {
    this.setState(prevState => ({
      isSidebarHidden: !prevState.isSidebarHidden
    }));
  };


  render() {
    return (
      <>
        <section style={{margin:"0",padding:"0"}} id="sidebar" className={this.state.isSidebarHidden ? 'hide' : ''}>

          <a href="##" className="brand">

            <br/>
            <ul className='brand'>
              <button id='togSide' className="toggle-sidebar" onClick={() => { this.handleSidebarToggle() }}>
                <TogSide />
              </button>
            </ul>
          </a>
          <br/>

          <ul className="side-menu">
            <li>
              <a href="##" className="active">
                <i className='bx bxs-dashboard icon'></i> Dashboard &nbsp;&nbsp; <PlusAnimate />
              </a>
            </li>
            <li className="divider" data-text="main">Main</li>
            <div>
              <li>
                <a onClick={(e) => this.handleDropdownClick(e)} href="##" ><i className='bx bxs-inbox icon' ></i> Finance <i><Arrow /></i></a>
                <ul className="side-dropdown">
                  <li><a href="##" >Revenue</a></li>
                  <li><a href="##" >CASY</a></li>
                  <li><a href="##" >Net Profit</a></li>
                  <li><a href="##" >Button</a></li>
                </ul>
              </li>
              <li><a href="##" ><i className='bx bxs-chart icon' ></i> Charts</a></li>
              <li><a href="##" ><i className='bx bxs-cart icon' ></i> E-Commerce</a></li>
              <li><a href="##" ><i className='bx bxs-analyse icon' ></i> Analytics</a></li>
              <li><a href="##" ><i className='bx bxs-contact icon' ></i> Contacts</a></li>
              <li><a href="##" ><i className='bx bx-mail-send icon' ></i> Mail-box</a></li>
              <li><a href="##" ><i className='bx bx-task icon' ></i> Tasks</a></li>
              <li><a href="##" ><i className='bx bxs-lock-alt icon' ></i> Authentication</a></li>
